import { Component, OnInit } from '@angular/core';
import { NgForm } from '@angular/forms';
import { materialize } from 'rxjs';
import { User } from '../shared/user.model';

import { UserService } from '../shared/user.service';

declare var M:any;

@Component({
  selector: 'app-user',
  templateUrl: './user.component.html',
  styleUrls: ['./user.component.css'],
  providers:  [UserService]
})
export class UserComponent {
  constructor(public userService:UserService) {}

  ngOnInit()
  {
    this.resetForm()
    this.getUserList()
  }


resetForm(form?:NgForm)
{
  if(form)
    form.reset();
    this.userService.selectedUser={
      _id:"",
      name:"",
      mail:"",
      age:undefined,
      number:undefined
    }
}


onSubmit(form : NgForm)
{
  if(form.value._id=="")
  {
this.userService.postUser(form.value).subscribe((res)=>{
  this.resetForm(form);
  this.getUserList()
  M.toast({html: 'Created Successfully!', classes: 'rounded'});
});
  }
  else
  {
    this.userService.updateUser(form.value).subscribe((res)=>{
      this.resetForm(form);
      this.getUserList()
      M.toast({html: 'Updated Successfully!', classes: 'rounded'});
    });
  }

}


getUserList()
{
  this.userService.getUserList().subscribe((res)=>{
    this.userService.users=res as User[]
  })
}


onEdit(user:User)
{
  this.userService.selectedUser=user
}

onDelete(user:User)
{
  this.userService.deleteUser(user)
}


}
<div class="row">
  <div class="col s12">
    <div class="card">
      <div class="card-content white-text">
        <div class="row">
          <div class="s5 col">
            <form #userForm="ngForm" (ngSubmit)="onSubmit(userForm)">
              <input type="hidden" name="_id" #_id="ngModel" [(ngModel)]="userService.selectedUser._id">
              <div class="row">
                <div class="input-field col s12">
                  <input type="text" name="name" #name="ngModel" [(ngModel)]="userService.selectedUser.name" placeholder="name :">
                  <label>Name :
                    <label class="red-text">*</label>
                  </label>
                </div>
              </div>
              <div class="row">
                <div class="input-field col s12">
                  <input type="text" name="mail" #name="ngModel" [(ngModel)]="userService.selectedUser.mail" placeholder="mail :">
                  <label>mail :
                    <label class="red-text">*</label>
                  </label>
                </div>
              </div>
              <div class="row">
                <div class="input-field col s12">
                  <input type="text" name="age" #name="ngModel" [(ngModel)]="userService.selectedUser.age" placeholder="age :">
                  <label>age :
                    <label class="red-text">*</label>
                  </label>
                </div>
              </div>
              <div class="row">
                <div class="input-field col s12">
                  <input type="text" name="number" #name="ngModel" [(ngModel)]="userService.selectedUser.number" placeholder="number :">
                  <label>number :
                    <label class="red-text">*</label>
                  </label>
                </div>
              </div>
              <div class="row">
                <div class="input-field col s12">
                  <button class="btn btn-custom right" type="button">Reset</button>
                  <button class="btn btn-custom right" type="submit">Submit</button>
                </div>
              </div>
            </form>
          </div>
          <div class="col s7">
            <table class="responsive-table highlight">
              <thead>
                <tr>
                  <th>name</th>
                  <th>mail</th>
                  <th>age</th>
                  <th>number</th>
                  <th></th>
                </tr>
              </thead>
              <tr *ngFor="let user of userService.users">
                <td>{{user.name}}</td>
                <td>{{user.mail}}</td>
                <td>{{user.age}}</td>
                <td>{{user.number}}</td>
                <td>
                  <a class="action-btn" (click)="onEdit(user)">
                    <i class="material-icons">edit</i>
                  </a>
                  <a class="action-btn" (click)="onDelete(user._id,user)">
                    <i class="material-icons">delete</i>
                  </a>
                </td>
              </tr>
            </table>
          </div>
        </div>
      </div>
    </div>
  </div>
</div>
